pragma solidity ^0.8.23;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import {SimpleAccount, IEntryPoint} from "./SimpleAccount.sol";
import {PublicKeyRegistry} from "./PublicKeyRegistry.sol";

/**
 * A sample factory contract for SimpleAccount
 * A UserOperations "initCode" holds the address of the factory, and a method call (to createAccount, in this sample factory).
 * The factory's createAccount returns the target account address even if it is already installed.
 * This way, the entryPoint.getSenderAddress() can be called either before or after the account is created.
 */
contract SimpleAccountFactory {
    SimpleAccount public immutable accountImplementation;

    constructor(
        IEntryPoint _entryPoint,
        address _proofVerifier,
        PublicKeyRegistry _publicKeyRegistry
    ) {
        accountImplementation = new SimpleAccount(
            _entryPoint,
            _proofVerifier,
            _publicKeyRegistry
        );
    }

    /**
     * create an account, and return its address.
     * returns the address even if the account is already deployed.
     * Note that during UserOperation execution, this method is called only if the account is not deployed.
     * This method returns an existing account address so that entryPoint.getSenderAddress() would work even after account creation
     */
    function createAccount(
        SimpleAccount.InitializeParams calldata params
    ) public returns (SimpleAccount ret) {
        // TODO: create2 address should depend only on accountId and jwt.aud (and maybe "jwt.iss"?)
        ret = SimpleAccount(
            payable(
                new ERC1967Proxy{salt: bytes32(0)}(
                    address(accountImplementation),
                    abi.encodeCall(SimpleAccount.initialize, params)
                )
            )
        );
    }

    /**
     * calculate the counterfactual address of this account as it would be returned by createAccount()
     */
    function getAccountAddress(
        SimpleAccount.InitializeParams calldata params
    ) public view returns (address) {
        return
            Create2.computeAddress(
                bytes32(0),
                keccak256(
                    abi.encodePacked(
                        type(ERC1967Proxy).creationCode,
                        abi.encode(
                            address(accountImplementation),
                            abi.encodeCall(SimpleAccount.initialize, (params))
                        )
                    )
                )
            );
    }
}
","keccak256":"0x0e2aa69d2d8a88006e35d424fb21ed006d095e1d06ff1b386782ad1c997b5f7a","license":"GPL-3.0"},"contracts/Strings.sol":{"content":"// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.27;

library Strings {
    bytes16 private constant HEX_DIGITS = "0123456789abcdef";

    /**
     * @dev The `value` string doesn't fit in the specified `length`.
     */
    error StringsInsufficientHexLength(uint256 value, uint256 length);

    function toHexStringWithoutPrefix(
        uint256 value,
        uint256 length
    ) internal pure returns (string memory) {
        uint256 localValue = value;
        bytes memory buffer = new bytes(2 * length);
        for (uint256 i = 2 * length + 1; i > 1; --i) {
            buffer[i - 2] = HEX_DIGITS[localValue & 0xf];
            localValue >>= 4;
        }
        if (localValue != 0) {
            revert StringsInsufficientHexLength(value, length);
        }
        return string(buffer);
    }

    function toHexStringWithoutPrefix(
        bytes32 value
    ) internal pure returns (string memory) {
        return toHexStringWithoutPrefix(uint256(value), 32);
    }
}
